    //    const updatedCategory = {links: belongedLinks, ...c}
    //    return updatedCategory
    //  }
    //})
    //const data = await rawdata.json()
    //console.log("data", params)
    return {
      props: {
        categorydata,
        currentCategoryData,
        tagdata,

      }, // will be passed to the page component as props
    }
}


export default CategoryPage
